import React, { useState } from "react";
import clsx from "clsx";

type TabsProps = {
  defaultValue: string;
  className?: string;
  children: React.ReactNode;
};

type TabChildProps = {
  activeTab?: string;
  setActiveTab?: (value: string) => void;
};

export const Tabs: React.FC<TabsProps> = ({ defaultValue, className, children }) => {
  const [activeTab, setActiveTab] = useState(defaultValue);

  return (
    <div className={clsx("tabs", className)}>
      {React.Children.map(children, (child) =>
        React.isValidElement<TabChildProps>(child)
          ? React.cloneElement(child, { activeTab, setActiveTab })
          : child
      )}
    </div>
  );
};

export const TabsList: React.FC<{ className?: string }> = ({ className, children }) => (
  <div className={clsx("flex space-x-2", className)}>{children}</div>
);

export const TabsTrigger: React.FC<{
  value: string;
  activeTab?: string;
  setActiveTab?: (value: string) => void;
  className?: string;
}> = ({ value, activeTab, setActiveTab, className, children }) => (
    
  <button
    className={clsx(
      "px-4 py-2 rounded-md",
      activeTab === value ? "bg-blue-500 text-white" : "bg-gray-200 text-black",
      className
    )}
    onClick={() => setActiveTab?.(value)}
  >
    <span className="font-medium">{value}</span>
    {children}
  </button>
);

export const TabsContent: React.FC<{ value: string; activeTab?: string; className?: string }> = ({
  value,
  activeTab,
  className,
  children,
}) => (activeTab === value ? <div className={clsx("mt-4", className)}>{children}</div> : null);
